    selectedQuestions : Question [] = [];
    selectedQuestionsNumbers : number[] = [];

    showQuestions (number: number) : Question [] {
        this.selectedQuestions = [];
        this.selectedQuestionsNumbers = [];
        let i:number=number;
        if (number<300) {
          while (i<30+number) {
            this.selectedQuestions.push(this.allQuestions[i]);
            i++;
          }
        } else {
          while (i<10+number) {
            this.selectedQuestions.push(this.allQuestions[i]);
            i++;
          }
        }
        return this.selectedQuestions;
    }

    prepareExam (bundesland: number) {
        this.selectedQuestions = [];
        this.selectedQuestionsNumbers = [];
        let i:number=0;
        while (i<30) {
            let flag = false;
            let newNumber: number;
            while (!flag) {
                newNumber = Math.floor(Math.random()*300);
                if (this.findNewNumber(newNumber)) {
                    flag = true;
                }
            }
            this.selectedQuestionsNumbers.push(newNumber);
            this.selectedQuestions.push(this.allQuestions[newNumber]);
            i++;
        }

        while (i<33) {
            let flag = false;
            let newNumber: number;
            while (!flag) {
                newNumber = Math.floor(Math.random()*10)+bundesland;
                if (this.findNewNumber(newNumber)) {
                    flag = true;
                }
            }
            this.selectedQuestionsNumbers.push(newNumber);
            this.selectedQuestions.push(this.allQuestions[newNumber]);
            i++;
        }
        return this.selectedQuestions;
    }

    findNewNumber(randomnumber: number) :boolean {
        for (let number of this.selectedQuestionsNumbers) {
            if (number == randomnumber) {
                return false;
            }
        }
        return true;
    }

